import React from 'react';
import './App.css';
import { CATEGORIES } from './data';
import Button from './Components/Button';
import TasksContainer from './Containers/TasksContainer';
import ToggleButton from './Components/ToggleButton';

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    filter: "All",
    clicked: false
  }

  filterTasks = (filterValue) => {

    this.setState({
      filter: filterValue,
    })
  }

  renderButtons = () => {
    return CATEGORIES.map(el => <Button clicked={this.state.clicked} filter={this.state.filter} value={el} filterTasks={this.filterTasks}/>)
  }

  toggleButton = () => {
    this.setState((prevState) => {
      return {
        clicked: !prevState.clicked
      }
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log('submit')
  }

  render() {
    return (
      <div className="App">
        < ToggleButton toggleButton={this.toggleButton} clicked={this.state.clicked}/>
        <h2>My tasks</h2>
        <div class="categories">
        <h5>category filters</h5>
        {this.renderButtons()}
        </div>
        < TasksContainer handleSubmit={this.handleSubmit} clicked={this.state.clicked} tasks={this.state.tasks} filterValue={this.state.filter}/>
      </div>
    );
  }
}


export default App;
