import React from 'react'
import Task from '../Components/Task'

class TasksContainer extends React.Component {

    state = {
        clicked: false
    }

    renderTask = () => {
        let tasks = this.props.filterValue === "All" ? this.props.tasks : this.props.tasks.filter(el => el.category === this.props.filterValue)
        return tasks.map(el => < Task task={el} clicked={this.state.clicked}/>)
    }

    render() {
        return(
            <div class="tasks">
                <h4>Tasks</h4>
                {this.renderTask()}
            </div>
        )
    }
}

export default TasksContainer
