import React from 'react';
import './App.css';
import { CATEGORIES } from './data';
import Button from './Components/Button'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  renderButtons = () => {
    return CATEGORIES.map(el => <button>{el}</button>)
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <h5>category filters</h5>
        {this.renderButtons()}
      </div>
    );
  }
}


export default App;
