import React from 'react'

class NewTaskForm extends React.Component {

    state = {
        value: ""
    }

    updateValue = (e) => {
        this.setState({
            value: e.target.value
        })
    }

    render() {
        return(
            this.props.clicked ? 
                <form className="new-task-form">
                    <input placeholder="New task details" type="text" value={this.state.value} onChange={this.updateValue}/>
                    <select>
                        <option>Code</option>
                        <option>Food</option>
                        <option>Money</option>
                        <option>Misc</option>
                    </select>
                    <input type="submit" value="Add task"/>
                </form>
                : null
        )
    }
}

export default NewTaskForm
