import React from 'react'
import SelectOptions from './TaskForm'

class TaskForm extends React.Component {
    state = {
        text: '',
        category: '',
    }

    renderCategoryOptions = () => {
        return this.props.categories.map(cat => <Category category={cat} 
            selected={this.props.selected}
            clickHandler={this.props.clickHandler}/>
        )
    }

    // renderCategoryOptions = () => {
    //     console.log('categories: ', this.props.categories)
    //     return this.props.categories.map(cat => <SelectOptions category={cat} />)
    // }

    render() {
        // console.log(this.props.categories)
        return <form className="new-task-form">
            <input type="text" placeholder="New task details" value={this.state.category}/>
            <select>{this.renderCategoryOptions()}</select>
            <input type="submit" value="Add task" />
        </form>
    }
}

export default TaskForm
